javascript:
function generateRandomColor() {
  return '#' + Math.floor(Math.random() * 16777215).toString(16).padStart(6, '0');
}

function createColorBox(color) {
  const colorBox = document.createElement('div');
  colorBox.className = 'color-box';
  colorBox.style.backgroundColor = color;

  const colorCode = document.createElement('span');
  colorCode.className = 'color-code';
  colorCode.textContent = color.toUpperCase();

  colorBox.appendChild(colorCode);

  colorBox.addEventListener('click', () => {
    navigator.clipboard.writeText(color.toUpperCase());

    // Visual feedback
    const originalText = colorCode.textContent;
    colorCode.textContent = 'COPIED!';
    colorBox.style.transform = 'scale(0.95)';

    setTimeout(() => {
      colorCode.textContent = originalText;
      colorBox.style.transform = '';
    }, 500);
  });

  return colorBox;
}

function generatePalette() {
  const palette = document.getElementById('palette');
  palette.innerHTML = '';

  for (let i = 0; i < 5; i++) {
    const color = generateRandomColor();
    const colorBox = createColorBox(color);
    palette.appendChild(colorBox);
  }
}

document.getElementById('generateBtn').addEventListener('click', generatePalette);

// Generate initial palette
generatePalette();

