// Animate card entrance
window.addEventListener('load', () => {
  const card = document.querySelector('.weather-card');
  card.style.transform = 'translateY(50px)';
  card.style.opacity = '0';
  card.style.transition = 'all 0.8s cubic-bezier(0.4, 0, 0.2, 1)';

  setTimeout(() => {
    card.style.transform = 'translateY(0)';
    card.style.opacity = '1';
  }, 100);
});

// Add interactive temperature conversion
const temperature = document.querySelector('.temperature');
let isCelsius = true;

temperature.addEventListener('click', () => {
  const currentTemp = parseInt(temperature.textContent);

  if (isCelsius) {
    const fahrenheit = Math.round((currentTemp * 9/5) + 32);
    temperature.textContent = fahrenheit + '째F';
  } else {
    const celsius = Math.round((currentTemp - 32) * 5/9);
    temperature.textContent = celsius + '째';
  }

  isCelsius = !isCelsius;

  // Add click animation
  temperature.style.transform = 'scale(0.95)';
  setTimeout(() => {
    temperature.style.transform = 'scale(1)';
  }, 150);
});

// Add cursor pointer for temperature
temperature.style.cursor = 'pointer';
temperature.title = 'Click to convert temperature';
